Scripts.OnClick:
PanelTemplates_SetTab(MetaHudOptionsFrame, self:GetID());
for i = 1, MetaHudO_NUMTABS do
if i == self:GetID() then
_G[self:GetName().."Text"]:Hide();
_G[self:GetName().."HighlightText"]:Hide();
_G[self:GetName().."DisabledText"]:Show();
_G["MetaHud_Tab"..i]:Show();
else
_G["MetaHudOptionsFrameTab"..i.."Text"]:Show();
_G["MetaHudOptionsFrameTab"..i.."HighlightText"]:Hide();
_G["MetaHudOptionsFrameTab"..i.."DisabledText"]:Hide();
_G["MetaHud_Tab"..i]:Hide();
end
end

Scripts.OnLoad:
local parent = self:GetParent();
self.setting_name = string.gsub( parent:GetName() , "MetaHud_Pos_", "");
self:RegisterForClicks("LeftButtonUp", "RightButtonUp");

Scripts.OnClick:
if(button == "RightButton") then
MetaHud:SetConfig(self.setting_name, MetaHud:GetConfig(self.setting_name) - 10);
else
MetaHud:SetConfig(self.setting_name, MetaHud:GetConfig(self.setting_name) - 1);
end
local parent = self:GetParent();
local text = _G[parent:GetName().."_Minitext"];
text:SetText( MetaHud:GetConfig(self.setting_name) );
MetaHud:init();

Scripts.OnLoad:
local parent = self:GetParent();
self.setting_name = string.gsub( parent:GetName() , "MetaHud_Pos_", "");
self:RegisterForClicks("LeftButtonUp", "RightButtonUp");

Scripts.OnClick:
if (button == "RightButton") then
MetaHud:SetConfig(self.setting_name, MetaHud:GetConfig(self.setting_name) + 10);
else
MetaHud:SetConfig(self.setting_name, MetaHud:GetConfig(self.setting_name) + 1);
end
local parent = self:GetParent();
local text = _G[parent:GetName().."_Minitext"];
text:SetText( MetaHud:GetConfig(self.setting_name) );
MetaHud:init();

Scripts.OnLoad:
local parent = self:GetParent();
self.setting_name = string.gsub( parent:GetName() , "MetaHud_Pos_", "");
_G[self:GetName().."Text"]:SetText( " "..(MetaHudO_locale[self.setting_name] or "No Locale") );
self:SetText( tonumber(MetaHud:GetConfig(self.setting_name)) or 0 );
self.oldvalue = self:GetText();

Scripts.OnTextChanged:
if self:GetNumber() == self:GetText() then
self.oldvalue = self:GetText();
end
self:SetText( self:GetNumber() or 0 );
MetaHud:SetConfig( self.setting_name, self:GetText() );

Scripts.OnEnter:
if ( self.tooltipText ) then
GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
GameTooltip:SetText(self.tooltipText, nil, nil, nil, nil, 1);
end

Scripts.OnTextChanged:
local parent = self:GetParent();
local scrollBar = _G[parent:GetName().."ScrollBar"]
parent:UpdateScrollChildRect();
local min, max = scrollBar:GetMinMaxValues();
if ( max > 0 and (self.max ~= max) ) then
self.max = max;
scrollBar:SetValue(max);
end

Scripts.OnEditFocusLost:
                                        local parent = self:GetParent();
                                        local name = string.gsub( parent:GetParent():GetName() , "MetaHud_Edit_", "");
                                        local dropdown = _G["MetaHud_Edit_"..name.."_Selection"];
                                        MetaHud:SetConfig( name , self:GetText() );

                                        UIDropDownMenu_SetSelectedID( dropdown, 1 );
                                        UIDropDownMenu_SetText(dropdown, MetaHudO_SELECTION[name]["table"][1]);

                                        for key, str in pairs(MetaHudO_SELECTION[name]["values"]) do
                                        if MetaHud:GetConfig(name) == str then
                                        UIDropDownMenu_SetSelectedID( dropdown, key );
                                        droptext = MetaHudO_SELECTION[name]["table"][key];
                                        UIDropDownMenu_SetText(dropdown, droptext);
                                        end
                                        end

                                        MetaHud:init();
                                        MetaHudO_updateTexts(name);
                                        MetaHudHelpFrame:Hide();

Scripts.OnShow:
                    local droptext;
                    local dropdown = _G[self:GetName().."_Selection"];

                    local label = _G[self:GetName().."_SelectionLabel"];
                    label:SetText(MetaHudO_locale[self.setting_name] or "no Locale");

                    local box = _G[self:GetName().."ScrollFrameText"];
                    box:SetText( MetaHud:GetConfig(self.setting_name) );

                    local tipptext = MetaHudO_SELECTION[self.setting_name]["ttt"];
                    dropdown.tooltipText = tipptext;

                    UIDropDownMenu_SetSelectedID( dropdown, 1 );
                    UIDropDownMenu_SetText(dropdown, MetaHudO_SELECTION[self.setting_name]["table"][1]);

                    for key, str in pairs(MetaHudO_SELECTION[self.setting_name]["values"]) do
                    if MetaHud:GetConfig(self.setting_name) == str then
                    UIDropDownMenu_SetSelectedID( dropdown, key );
                    droptext = MetaHudO_SELECTION[self.setting_name]["table"][key];
                    UIDropDownMenu_SetText(dropdown, droptext);
                    end
                    end
